$(function() {
    function b(s) {
        var p = null,
        r = $("#" + s),
        n = r.children().eq(0).find("img"),
        q = r.children().eq(1).find("li"),
        l = n.length,
        o = l - 2,
        m = 1;
        p = new Swipe(document.getElementById(s), {
            startSlide: 0,
            auto: 4000,
            speed: 300,
            callback: function(t, u) {
                q.removeClass("active").eq(t).addClass("active");
                if (m <= o) {
                    if (t >= 1 && t <= o) {
                        var v = n.eq(t + 1);
                        if (v.length > 0) {
                            src = v.attr("data-src");
                            v.attr("src", src)
                        }
                    }
                    m++
                }
            }
        })
    }
  
    setTimeout(function() {
        b("imgSwipe");
    },1);
  
});